	public static function guess( $for, $fallback = '' )
	{
		switch ( $for ) {

			case 'contact':
				return [ 'misc-16', 'globe2' ];

			case 'twitter':
			case 'facebook':
				return [ 'social-logos', $for ];

			// case 'youtube':
			case 'telegram':
				return [ 'misc-32', $for ];
			break;

			case 'mobile':
			case 'phone':
				return [ 'octicons', 'device-mobile' ];
		}

		return $fallback;
	}

	// @SEE: https://core.trac.wordpress.org/ticket/40175
	// @SEE: https://developer.mozilla.org/en-US/docs/Web/HTTP/Basics_of_HTTP/MIME_types/Common_types
	public static function guessByMIME( $mime, $fallback = '' )
	{
		if ( ! $mime )
			return $fallback;

		switch ( $mime ) {

			case 'audio/mpeg'   : return [ 'misc-16', 'filetype-mp3'  ];
			case 'text/csv'     : return [ 'misc-16', 'filetype-csv'  ];  // NOTE: the mime-type in wp-core does not work, WTF?!
			case 'text/markdown': return [ 'misc-16', 'filetype-md'   ];
			case 'text/xml'     : return [ 'misc-16', 'filetype-xml'  ];

			case 'application/epub+zip'          : return [ 'misc-32', 'epub'          ];  // NOTE: Bootstrap Icons currently has no `epub`
			case 'application/msword'            : return [ 'misc-16', 'filetype-doc'  ];
			case 'application/pdf'               : return [ 'misc-16', 'filetype-pdf'  ];
			case 'application/vnd.ms-excel'      : return [ 'misc-16', 'filetype-xls'  ];  // @SEE: https://core.trac.wordpress.org/ticket/39550#comment:156
			case 'application/x-mobipocket-ebook': return [ 'misc-16', 'book'          ];  // NOTE: Bootstrap Icons currently has no `mobi`

			case 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet'      : return [ 'misc-16', 'filetype-xlsx' ];
			case 'application/vnd.openxmlformats-officedocument.wordprocessingml.document': return [ 'misc-16', 'filetype-docx' ];
		}

		return $fallback;
	}

	public static function get( $icon, $group, $extra = [] )
	{
		return '<span data-icon="svg" class="'.HTML::prepClass(
			static::BASE.'-icon',
			'-iconsvg',
			sprintf( 'icon-%s-%s', $group, $icon ),
			$extra
		).'"><svg><use xlink:href="#icon-'.$group.'-'.$icon.'"></use></svg></span>';
	}

	// FIXME: use CSS background
	// SEE: `#adminmenu div.wp-menu-image.svg`
	// SEE: https://stackoverflow.com/a/19570011
	public static function wrapBase64( $data, $extra = [] )
	{
		return '<span data-icon="base64" class="'.HTML::prepClass(
			static::BASE.'-icon',
			'-iconbase64',
			$extra
		).'"><img src="'.$data.'" /></span>';
	}

	public static function wrapURL( $url, $extra = [] )
	{
		return '<span data-icon="url" class="'.HTML::prepClass(
			static::BASE.'-icon',
			'-iconurl',
			$extra
		).'"><img src="'.$url.'" /></span>';
	}

	// @REF: https://stackoverflow.com/a/42265057
	// The fill="black" is important. Without the fill attribute, WordPress wont be able to match the admin color scheme
	public static function getBase64( $icon, $group )
	{
		if ( isset( self::$icons[$group][$icon] ) )
			return 'data:image/svg+xml;base64,'.base64_encode(
				'<svg width="20" height="20" viewBox="'.self::$viewboxes[$group].'" xmlns="http://www.w3.org/2000/svg">'
				.str_replace( '<path d="', '<path fill="black" d="', self::$icons[$group][$icon] )
				// .preg_replace( '/abc/', '', self::$icons[$group][$icon], 1 )
				.'</svg>'
			);

		return FALSE;
	}

	public static function printSprites( $icons = [] )
	{
		if ( empty( $icons ) )
			return;

		echo '<svg style="position: absolute; width: 0; height: 0; overflow: hidden" version="1.1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink"><defs>';

		foreach ( $icons as $icon )
			if ( isset( self::$icons[$icon['group']][$icon['icon']] ) )
				// echo '<symbol id="icon-'.$icon['group'].'-'.$icon['icon'].'" viewBox="'.self::$viewboxes[$icon['group']].'"><title>'.$icon['icon'].'</title>'.self::$icons[$icon['group']][$icon['icon']].'</symbol>';
				echo '<symbol id="icon-'.$icon['group'].'-'.$icon['icon'].'" viewBox="'.self::$viewboxes[$icon['group']].'">'.self::$icons[$icon['group']][$icon['icon']].'</symbol>';

		echo '</defs></svg>';
	}
